svg.script:
(function() {
  var texts = ["Student", "Learner", "Builder"];
  var i = 0, j = 0, forward = true, pause = 0;
  var speed = 100, hold = 1000;
  var el = document.getElementById('typewriter');
  function type() {
    if (forward) {
      if (j < texts[i].length) {
        el.textContent = texts[i].slice(0, ++j);
        setTimeout(type, speed);
      } else {
        forward = false;
        pause = hold;
        setTimeout(type, pause);
      }
    } else {
      if (j > 0) {
        el.textContent = texts[i].slice(0, --j);
        setTimeout(type, speed / 2);
      } else {
        forward = true;
        i = (i + 1) % texts.length;
        setTimeout(type, speed);
      }
    }
  }
  type();
})();
